import React from "react";
import "./about.css";
import AboutImg from "../../assets/about.jpg";
import CV from "../../assets/John-Cv.pdf"
import Info from "./Info";

function About(){
    return(
        <section className="about section" id="about">
            <h2 className="section__title">About Me</h2>
            <span className="section__subtitle">My Introduction</span>

            <div className="about__container container grid">
                <img src={AboutImg} alt="" className="about__img"></img>
                <div className="about__data">
                    <Info />

                    <p classnamer="about__description">Frontend developer, I create web pages with UI / UX user interface, I have years of experience and many clients are happy with the projects carried out.</p>
                    <a download="" href={CV} className="button button--flex"></a>
                </div>
            </div>
        </section>
    )
}

export default About;
